import { TarotCard } from '../types';

const tarotDeck: TarotCard[] = [
  // Major Arcana
  { id: 0, name: { en: "The Fool", ru: "Шут" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/00_fool.png", description: { en: "Beginnings, innocence, spontaneity, a free spirit.", ru: "Начинания, невинность, спонтанность, свободный дух." }, longDescription: { en: "The Fool stands on the precipice of a new adventure, unburdened by the past and filled with limitless potential. It represents a leap of faith, an embrace of the unknown, and the courage to begin a journey. This card encourages you to trust your instincts and follow your heart, even if the path ahead is unclear.", ru: "Шут стоит на краю пропасти нового приключения, не обремененный прошлым и полный безграничного потенциала. Он представляет собой прыжок веры, объятие неизвестного и смелость начать путешествие. Эта карта призывает вас доверять своим инстинктам и следовать своему сердцу, даже если дальнейший путь неясен." } },
  { id: 1, name: { en: "The Magician", ru: "Маг" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/01_magician.png", description: { en: "Manifestation, resourcefulness, power, inspired action.", ru: "Проявление, находчивость, сила, вдохновенное действие." }, longDescription: { en: "The Magician is the master of manifestation, wielding all the tools necessary to turn ideas into reality. This card symbolizes resourcefulness, skill, and the power of will. It indicates that you have the power and ability to create your own world. It's a sign to take inspired action and make your intentions manifest.", ru: "Маг — мастер проявления, владеющий всеми необходимыми инструментами для превращения идей в реальность. Эта карта символизирует находчивость, мастерство и силу воли. Она указывает на то, что у вас есть сила и способность создавать свой собственный мир. Это знак к вдохновенному действию и воплощению ваших намерений." } },
  { id: 2, name: { en: "The High Priestess", ru: "Верховная Жрица" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/02_high_priestess.png", description: { en: "Intuition, sacred knowledge, divine feminine, the subconscious mind.", ru: "Интуиция, священное знание, божественное женское начало, подсознание." }, longDescription: { en: "The High Priestess is the guardian of the subconscious and sacred knowledge. She represents intuition, mystery, and the divine feminine. This card suggests it's time to listen to your inner voice and trust your intuition. There are hidden truths to be discovered, not through logic, but through looking within.", ru: "Верховная Жрица — хранительница подсознания и священных знаний. Она олицетворяет интуицию, тайну и божественное женское начало. Эта карта предполагает, что пришло время прислушаться к своему внутреннему голосу и довериться интуиции. Есть скрытые истины, которые нужно открыть не с помощью логики, а заглянув внутрь себя." } },
  { id: 3, name: { en: "The Empress", ru: "Императрица" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/03_empress.png", description: { en: "Femininity, beauty, nature, nurturing, abundance.", ru: "Женственность, красота, природа, забота, изобилие." }, longDescription: { en: "The Empress embodies nurturing, abundance, and a deep connection to the natural world. She is a symbol of fertility, creativity, and sensual pleasure. This card encourages you to connect with your feminine side, enjoy the beauty around you, and nurture yourself and others. It signifies a period of growth and abundance.", ru: "Императрица олицетворяет заботу, изобилие и глубокую связь с миром природы. Она является символом плодородия, творчества и чувственного удовольствия. Эта карта призывает вас соединиться со своей женской стороной, наслаждаться красотой вокруг и заботиться о себе и других. Она знаменует период роста и изобилия." } },
  { id: 4, name: { en: "The Emperor", ru: "Император" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/04_emperor.png", description: { en: "Authority, establishment, structure, a father figure.", ru: "Власть, учреждение, структура, отцовская фигура." }, longDescription: { en: "The Emperor represents authority, structure, and control. He is a father figure who brings order to chaos and establishes solid foundations. This card signifies a need for discipline, logic, and strategic planning. It suggests taking control of a situation, setting boundaries, and leading with confidence and a clear vision.", ru: "Император представляет власть, структуру и контроль. Он — отцовская фигура, которая наводит порядок в хаосе и закладывает прочные основы. Эта карта означает потребность в дисциплине, логике и стратегическом планировании. Она предлагает взять ситуацию под контроль, установить границы и руководить с уверенностью и ясным видением." } },
  { id: 5, name: { en: "The Hierophant", ru: "Иерофант" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/05_hierophant.png", description: { en: "Spiritual wisdom, religious beliefs, conformity, tradition, institutions.", ru: "Духовная мудрость, религиозные верования, конформизм, традиции, институты." }, longDescription: { en: "The Hierophant is a bridge between the divine and humanity, representing tradition, spiritual wisdom, and established institutions. This card can signify a mentor or teacher, or a connection to a group with shared beliefs. It suggests following established paths, honoring tradition, and seeking knowledge from trusted sources.", ru: "Иерофант — это мост между божественным и человечеством, представляющий традиции, духовную мудрость и устоявшиеся институты. Эта карта может означать наставника или учителя, или связь с группой с общими убеждениями. Она предлагает следовать проторенными путями, чтить традиции и искать знания из проверенных источников." } },
  { id: 6, name: { en: "The Lovers", ru: "Влюбленные" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/06_lovers.png", description: { en: "Love, harmony, relationships, values alignment, choices.", ru: "Любовь, гармония, отношения, соответствие ценностей, выбор." }, longDescription: { en: "The Lovers card represents deep connection, harmony, and significant choices. It often signifies a romantic partnership but can also point to any relationship built on mutual respect and love. This card is also about aligning with your core values and making choices that are true to yourself. It asks you to consider what you truly stand for.", ru: "Карта Влюбленные представляет глубокую связь, гармонию и важный выбор. Она часто означает романтическое партнерство, но также может указывать на любые отношения, построенные на взаимном уважении и любви. Эта карта также говорит о следовании своим основным ценностям и принятии решений, которые верны вам. Она просит вас задуматься о том, за что вы действительно стоите." } },
  { id: 7, name: { en: "The Chariot", ru: "Колесница" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/07_chariot.png", description: { en: "Control, willpower, success, action, determination.", ru: "Контроль, сила воли, успех, действие, решимость." }, longDescription: { en: "The Chariot is a symbol of determination, willpower, and victory. It represents successfully overcoming challenges through assertiveness and control. This card indicates a time of great forward momentum. By harnessing opposing forces and directing them with a clear purpose, you can achieve your goals and drive towards success.", ru: "Колесница — это символ решимости, силы воли и победы. Она представляет успешное преодоление трудностей благодаря настойчивости и контролю. Эта карта указывает на время большого движения вперед. Управляя противоборствующими силами и направляя их с ясной целью, вы можете достичь своих целей и двигаться к успеху." } },
  { id: 8, name: { en: "Strength", ru: "Сила" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/08_strength.png", description: { en: "Strength, courage, persuasion, influence, compassion.", ru: "Сила, мужество, убеждение, влияние, сострадание." }, longDescription: { en: "The Strength card represents not brute force, but inner strength, courage, and compassion. It depicts mastering one's primal urges through gentleness and patience. This card encourages you to approach situations with calm confidence and persuasion rather than aggression. It is a testament to the power of a gentle heart and a brave spirit.", ru: "Карта Сила представляет не грубую силу, а внутреннюю силу, мужество и сострадание. Она изображает укрощение своих первобытных инстинктов с помощью мягкости и терпения. Эта карта призывает вас подходить к ситуациям со спокойной уверенностью и убеждением, а не с агрессией. Это свидетельство силы нежного сердца и храброго духа." } },
  { id: 9, name: { en: "The Hermit", ru: "Отшельник" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/09_hermit.png", description: { en: "Soul-searching, introspection, being alone, inner guidance.", ru: "Самоанализ, интроспекция, одиночество, внутреннее руководство." }, longDescription: { en: "The Hermit retreats from the outside world to seek answers within. This card symbolizes introspection, soul-searching, and inner guidance. It suggests that you need a period of solitude to reflect and gain a deeper understanding of yourself. The Hermit's lamp is the light of wisdom that illuminates the path forward.", ru: "Отшельник удаляется от внешнего мира, чтобы найти ответы внутри себя. Эта карта символизирует самоанализ, поиск души и внутреннее руководство. Она предполагает, что вам нужен период уединения, чтобы поразмыслить и глубже понять себя. Лампа Отшельника — это свет мудрости, который освещает путь вперед." } },
  { id: 10, name: { en: "Wheel of Fortune", ru: "Колесо Фортуны" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/10_wheel_of_fortune.png", description: { en: "Good luck, karma, life cycles, destiny, a turning point.", ru: "Удача, карма, жизненные циклы, судьба, поворотный момент." }, longDescription: { en: "The Wheel of Fortune represents the cyclical nature of life, destiny, and turning points. It reminds us that life is always in motion, with ups and downs. This card can signify a change in luck or a major shift in your life's direction. It teaches the lesson of acceptance and adapting to the ever-changing flow of the universe.", ru: "Колесо Фортуны представляет цикличность жизни, судьбу и поворотные моменты. Оно напоминает нам, что жизнь всегда в движении, со взлетами и падениями. Эта карта может означать изменение удачи или серьезный сдвиг в направлении вашей жизни. Она учит уроку принятия и адаптации к постоянно меняющемуся потоку вселенной." } },
  { id: 11, name: { en: "Justice", ru: "Справедливость" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/11_justice.png", description: { en: "Justice, fairness, truth, cause and effect, law.", ru: "Справедливость, честность, правда, причина и следствие, закон." }, longDescription: { en: "The Justice card is about truth, fairness, and the law of cause and effect. It indicates that your actions have consequences, and a balanced, objective perspective is needed. This card calls for honesty and integrity. It may signify a legal matter or a decision that requires you to weigh all sides carefully to find the most equitable outcome.", ru: "Карта Справедливость — это истина, честность и закон причины и следствия. Она указывает на то, что ваши действия имеют последствия, и необходим сбалансированный, объективный взгляд. Эта карта призывает к честности и порядочности. Она может означать юридический вопрос или решение, которое требует от вас тщательного взвешивания всех сторон для нахождения наиболее справедливого результата." } },
  { id: 12, name: { en: "The Hanged Man", ru: "Повешенный" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/12_hanged_man.png", description: { en: "Pause, surrender, letting go, new perspectives.", ru: "Пауза, сдача, отпускание, новые перспективы." }, longDescription: { en: "The Hanged Man represents a pause, surrender, and gaining new perspectives by seeing things from a different angle. It is a card of voluntary suspension, not punishment. It suggests that you may need to let go of old patterns or release control to move forward. Sometimes, progress is made by standing still.", ru: "Повешенный представляет паузу, сдачу и обретение новых перспектив, взглянув на вещи под другим углом. Это карта добровольного подвешивания, а не наказания. Она предполагает, что вам может потребоваться отпустить старые шаблоны или ослабить контроль, чтобы двигаться вперед. Иногда прогресс достигается, стоя на месте." } },
  { id: 13, name: { en: "Death", ru: "Смерть" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/13_death.png", description: { en: "Endings, change, transformation, transition.", ru: "Окончания, изменения, трансформация, переход." }, longDescription: { en: "The Death card is rarely about literal death. It symbolizes powerful endings, transformation, and profound change. It signifies the closing of one chapter to make way for a new one. While this transition can be difficult, it is a necessary part of life's cycle, clearing the way for new growth and opportunities.", ru: "Карта Смерть редко говорит о буквальной смерти. Она символизирует мощные окончания, трансформацию и глубокие перемены. Она означает закрытие одной главы, чтобы освободить место для новой. Хотя этот переход может быть трудным, он является необходимой частью жизненного цикла, расчищая путь для нового роста и возможностей." } },
  { id: 14, name: { en: "Temperance", ru: "Умеренность" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/14_temperance.png", description: { en: "Balance, moderation, patience, purpose.", ru: "Баланс, умеренность, терпение, цель." }, longDescription: { en: "Temperance is the card of balance, moderation, and finding the middle path. It represents the blending of different elements to create something new and harmonious. This card advises patience and a calm approach. It's about finding your inner purpose and maintaining equilibrium in your life.", ru: "Умеренность — это карта баланса, сдержанности и нахождения среднего пути. Она представляет смешение различных элементов для создания чего-то нового и гармоничного. Эта карта советует терпение и спокойный подход. Она говорит о поиске своего внутреннего предназначения и поддержании равновесия в жизни." } },
  { id: 15, name: { en: "The Devil", ru: "Дьявол" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/15_devil.png", description: { en: "Shadow self, attachment, addiction, restriction, sexuality.", ru: "Теневое Я, привязанность, зависимость, ограничение, сексуальность." }, longDescription: { en: "The Devil card represents the shadow self, addiction, and unhealthy attachments. It points to feelings of being trapped by materialism, negative patterns, or limiting beliefs. This card is a call to confront your shadow and break free from the chains that bind you. Acknowledging these restrictions is the first step to liberation.", ru: "Карта Дьявол представляет теневое Я, зависимость и нездоровые привязанности. Она указывает на чувство ловушки, вызванное материализмом, негативными шаблонами или ограничивающими убеждениями. Эта карта — призыв встретиться со своей тенью и освободиться от цепей, которые вас связывают. Признание этих ограничений — первый шаг к освобождению." } },
  { id: 16, name: { en: "The Tower", ru: "Башня" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/16_tower.png", description: { en: "Sudden change, upheaval, chaos, revelation, awakening.", ru: "Внезапные перемены, потрясения, хаос, откровение, пробуждение." }, longDescription: { en: "The Tower signifies sudden, dramatic change and upheaval. It represents the destruction of false structures and beliefs, leading to a moment of shocking revelation. While chaotic and often painful, the Tower's destruction is necessary. It clears away what is no longer serving you, allowing you to rebuild on a more authentic and solid foundation.", ru: "Башня означает внезапные, драматические перемены и потрясения. Она представляет разрушение ложных структур и убеждений, ведущее к моменту шокирующего откровения. Хотя это хаотично и часто болезненно, разрушение Башни необходимо. Оно убирает то, что больше вам не служит, позволяя вам перестроиться на более подлинном и прочном фундаменте." } },
  { id: 17, name: { en: "The Star", ru: "Звезда" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/17_star.png", description: { en: "Hope, faith, purpose, renewal, spirituality.", ru: "Надежда, вера, цель, обновление, духовность." }, longDescription: { en: "The Star is a beacon of hope, inspiration, and renewal after a period of turmoil. It represents faith in the future and a deep sense of purpose. This card encourages you to trust in the universe and yourself. It is a time for healing, spiritual connection, and feeling blessed by the cosmos.", ru: "Звезда — это маяк надежды, вдохновения и обновления после периода смуты. Она представляет веру в будущее и глубокое чувство цели. Эта карта призывает вас доверять вселенной и себе. Это время для исцеления, духовной связи и ощущения благословения космоса." } },
  { id: 18, name: { en: "The Moon", ru: "Луна" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/18_moon.png", description: { en: "Illusion, fear, anxiety, subconscious, intuition.", ru: "Иллюзия, страх, тревога, подсознание, интуиция." }, longDescription: { en: "The Moon illuminates the world of the subconscious, intuition, and illusion. It represents fears and anxieties that may be hidden in the shadows of your mind. This card asks you to trust your intuition to navigate through a period of uncertainty. Things may not be as they seem, so listen carefully to your inner guidance.", ru: "Луна освещает мир подсознания, интуиции и иллюзий. Она представляет страхи и тревоги, которые могут быть скрыты в тени вашего разума. Эта карта просит вас доверять своей интуиции, чтобы пройти через период неопределенности. Вещи могут быть не такими, какими кажутся, поэтому внимательно прислушивайтесь к своему внутреннему руководству." } },
  { id: 19, name: { en: "The Sun", ru: "Солнце" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/19_sun.png", description: { en: "Positivity, fun, warmth, success, vitality.", ru: "Позитив, веселье, тепло, успех, жизненная сила." }, longDescription: { en: "The Sun is the embodiment of positivity, success, and vitality. It represents clarity, joy, and a radiant sense of self-confidence. This card brings warmth and optimism, illuminating your path and filling you with energy. It is one of the most positive cards in the deck, heralding a time of happiness and achievement.", ru: "Солнце — это воплощение позитива, успеха и жизненной силы. Оно представляет ясность, радость и лучезарное чувство уверенности в себе. Эта карта приносит тепло и оптимизм, освещая ваш путь и наполняя вас энергией. Это одна из самых позитивных карт в колоде, предвещающая время счастья и достижений." } },
  { id: 20, name: { en: "Judgement", ru: "Суд" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/20_judgement.png", description: { en: "Judgement, rebirth, inner calling, absolution.", ru: "Суд, возрождение, внутренний зов, отпущение грехов." }, longDescription: { en: "The Judgement card signifies a spiritual awakening, a rebirth, and an inner calling. It represents a moment of reckoning where you must evaluate your past actions and make a significant decision. This card is about absolution and forgiveness, allowing you to rise to a new level of consciousness and embrace your true purpose.", ru: "Карта Суд означает духовное пробуждение, возрождение и внутренний зов. Она представляет момент расплаты, когда вы должны оценить свои прошлые действия и принять важное решение. Эта карта говорит об отпущении грехов и прощении, позволяя вам подняться на новый уровень сознания и принять свое истинное предназначение." } },
  { id: 21, name: { en: "The World", ru: "Мир" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/21_world.png", description: { en: "Completion, integration, accomplishment, travel.", ru: "Завершение, интеграция, достижение, путешествие." }, longDescription: { en: "The World represents the successful completion of a cycle, a sense of integration, and profound accomplishment. It is the final card of the Major Arcana, symbolizing the end of a long journey. You have learned your lessons and achieved a state of wholeness. It's a time to celebrate your success before embarking on the next cycle.", ru: "Мир представляет успешное завершение цикла, чувство интеграции и глубокое достижение. Это последняя карта Старших Арканов, символизирующая конец долгого пути. Вы усвоили свои уроки и достигли состояния целостности. Это время праздновать свой успех, прежде чем приступить к следующему циклу." } },
  { id: 22, name: { en: "The Hero", ru: "Герой" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/22_hero.png", description: { en: "The journey's end and new beginning. Mastery and wisdom.", ru: "Конец пути и новое начало. Мастерство и мудрость." }, longDescription: { en: "The Hero is the culmination of the journey. Having integrated all the lessons of the Arcana, you have achieved mastery and wisdom. This card represents not just an end, but also a new beginning from a higher state of awareness. You are the master of your own destiny, ready to apply your hard-won knowledge to the world.", ru: "Герой — это кульминация путешествия. Интегрировав все уроки Арканов, вы достигли мастерства и мудрости. Эта карта представляет не просто конец, но и новое начало с более высокого уровня осознанности. Вы — хозяин своей судьбы, готовый применить свои с трудом завоеванные знания в мире." } },
  { id: 23, name: { en: "White Card", ru: "Белая Карта" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/23_white_card.png", description: { en: "Unlimited potential, the unwritten story. A question to the universe.", ru: "Безграничный потенциал, ненаписанная история. Вопрос ко вселенной." }, longDescription: { en: "The White Card is the unwritten story, the space between breaths. It represents unlimited potential and the freedom to create any future you desire. It is a question posed to the universe, and an invitation for you to provide the answer. When this card appears, the outcome is entirely in your hands. What will you create?", ru: "Белая Карта — это ненаписанная история, пространство между вдохами. Она представляет безграничный потенциал и свободу создавать любое будущее, которое вы пожелаете. Это вопрос, заданный вселенной, и приглашение вам дать на него ответ. Когда появляется эта карта, исход полностью в ваших руках. Что вы создадите?" } },
  
  // Wands
  { id: 24, name: { en: "Ace of Wands", ru: "Туз Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_01_ace.png", description: { en: "Inspiration, new opportunities, growth, potential.", ru: "Вдохновение, новые возможности, рост, потенциал." }, longDescription: { en: "The Ace of Wands is a spark of inspiration and new potential. It represents the beginning of a creative project, a new passion, or a surge of energy. This card encourages you to seize the new opportunity being presented and follow your inspiration with enthusiasm. The seed of a great idea has been planted.", ru: "Туз Жезлов — это искра вдохновения и нового потенциала. Он представляет начало творческого проекта, новую страсть или прилив энергии. Эта карта призывает вас воспользоваться новой возможностью и с энтузиазмом следовать своему вдохновению. Семя великой идеи посажено." } },
  { id: 25, name: { en: "Two of Wands", ru: "Двойка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_02_two.png", description: { en: "Future planning, progress, decisions, discovery.", ru: "Планирование будущего, прогресс, решения, открытия." }, longDescription: { en: "The Two of Wands is about future planning and making bold decisions. You have already achieved some success, and now you are looking to the horizon for your next move. This card signifies progress and discovery. It's time to step out of your comfort zone and explore the world of possibilities you've created for yourself.", ru: "Двойка Жезлов говорит о планировании будущего и принятии смелых решений. Вы уже достигли некоторого успеха, и теперь вы смотрите на горизонт в поисках своего следующего шага. Эта карта означает прогресс и открытия. Пришло время выйти из зоны комфорта и исследовать мир возможностей, который вы для себя создали." } },
  { id: 26, name: { en: "Three of Wands", ru: "Тройка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_03_three.png", description: { en: "Preparation, foresight, enterprise, expansion.", ru: "Подготовка, предвидение, предприимчивость, расширение." }, longDescription: { en: "The Three of Wands indicates that your plans are now in motion, and you are beginning to see the first signs of success. It represents foresight, preparation, and expansion. Your ships are coming in. This card encourages you to think long-term and embrace the opportunities for growth that are on their way.", ru: "Тройка Жезлов указывает на то, что ваши планы уже в движении, и вы начинаете видеть первые признаки успеха. Она представляет предвидение, подготовку и расширение. Ваши корабли прибывают. Эта карта призывает вас думать о долгосрочной перспективе и использовать возможности для роста, которые уже на подходе." } },
  { id: 27, name: { en: "Four of Wands", ru: "Четверка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_04_four.png", description: { en: "Celebration, joy, harmony, relaxation, homecoming.", ru: "Празднование, радость, гармония, расслабление, возвращение домой." }, longDescription: { en: "The Four of Wands is a card of celebration, harmony, and homecoming. It represents a joyful milestone, a time of stability and happiness with loved ones. This card signifies a well-deserved period of rest and celebration after hard work. It's a time to appreciate the strong foundations you have built.", ru: "Четверка Жезлов — это карта празднования, гармонии и возвращения домой. Она представляет радостную веху, время стабильности и счастья с близкими. Эта карта означает заслуженный период отдыха и празднования после тяжелой работы. Пришло время оценить прочный фундамент, который вы построили." } },
  { id: 28, name: { en: "Five of Wands", ru: "Пятерка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_05_five.png", description: { en: "Conflict, disagreements, competition, tension, diversity.", ru: "Конфликт, разногласия, соревнование, напряжение, разнообразие." }, longDescription: { en: "The Five of Wands points to conflict, competition, and disagreements. There may be tension or a clash of egos around you. However, this conflict is often minor and can be a source of creative energy if channeled correctly. It's a call to find a way to work with diverse opinions and turn competition into collaboration.", ru: "Пятерка Жезлов указывает на конфликт, соревнование и разногласия. Вокруг вас может быть напряжение или столкновение эго. Однако этот конфликт часто незначителен и может стать источником творческой энергии, если его правильно направить. Это призыв найти способ работать с различными мнениями и превратить соревнование в сотрудничество." } },
  { id: 29, name: { en: "Six of Wands", ru: "Шестерка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_06_six.png", description: { en: "Public recognition, victory, progress, self-confidence.", ru: "Общественное признание, победа, прогресс, уверенность в себе." }, longDescription: { en: "The Six of Wands is a card of victory, public recognition, and success. You have overcome challenges and are now being celebrated for your achievements. This card brings a boost of self-confidence and encouragement. It's a time to be proud of your progress and enjoy the spotlight you have earned.", ru: "Шестерка Жезлов — это карта победы, общественного признания и успеха. Вы преодолели трудности и теперь вас чествуют за ваши достижения. Эта карта приносит прилив уверенности в себе и ободрение. Пришло время гордиться своим прогрессом и наслаждаться заслуженным вниманием." } },
  { id: 30, name: { en: "Seven of Wands", ru: "Семерка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_07_seven.png", description: { en: "Challenge, competition, perseverance, protective.", ru: "Вызов, соревнование, настойчивость, защита." }, longDescription: { en: "The Seven of Wands represents standing your ground in the face of challenges. You may be in a competitive situation where you need to defend your position. This card calls for perseverance and courage. Believe in yourself and what you stand for, and you will be able to maintain your advantage.", ru: "Семерка Жезлов представляет отстаивание своей позиции перед лицом трудностей. Вы можете оказаться в конкурентной ситуации, где вам нужно защищать свое положение. Эта карта призывает к настойчивости и мужеству. Верьте в себя и в то, за что вы стоите, и вы сможете сохранить свое преимущество." } },
  { id: 31, name: { en: "Eight of Wands", ru: "Восьмерка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_08_eight.png", description: { en: "Action, movement, quick decisions, alignment.", ru: "Действие, движение, быстрые решения, выравнивание." }, longDescription: { en: "The Eight of Wands signifies rapid action, movement, and progress. Events are likely to unfold quickly, bringing important news or decisions. This card indicates that obstacles are being removed, and it's a time for swift advancement towards your goals. Be ready to act decisively.", ru: "Восьмерка Жезлов означает быстрое действие, движение и прогресс. События, вероятно, будут разворачиваться стремительно, принося важные новости или решения. Эта карта указывает на то, что препятствия устранены, и это время для быстрого продвижения к вашим целям. Будьте готовы действовать решительно." } },
  { id: 32, name: { en: "Nine of Wands", ru: "Девятка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_09_nine.png", description: { en: "Resilience, courage, persistence, test of faith, boundaries.", ru: "Стойкость, мужество, настойчивость, испытание веры, границы." }, longDescription: { en: "The Nine of Wands is a card of resilience and persistence. You may be battle-weary but you are not defeated. This card signifies the final test of your faith and courage before you reach your goal. It's a reminder to stay strong, protect your boundaries, and gather your last reserves of strength for the final push.", ru: "Девятка Жезлов — это карта стойкости и настойчивости. Вы можете быть утомлены битвой, но вы не побеждены. Эта карта означает последнее испытание вашей веры и мужества перед достижением цели. Это напоминание оставаться сильным, защищать свои границы и собрать последние резервы сил для финального рывка." } },
  { id: 33, name: { en: "Ten of Wands", ru: "Десятка Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_10_ten.png", description: { en: "Burden, responsibility, hard work, stress, achievement.", ru: "Бремя, ответственность, тяжелая работа, стресс, достижение." }, longDescription: { en: "The Ten of Wands indicates that you have taken on a heavy burden of responsibility. While you have achieved success, you may be feeling overwhelmed and stressed. This card is a reminder to evaluate your commitments. It may be time to delegate or release some of your burdens to avoid burnout.", ru: "Десятка Жезлов указывает на то, что вы взвалили на себя тяжелое бремя ответственности. Хотя вы достигли успеха, вы можете чувствовать себя перегруженным и напряженным. Эта карта — напоминание о необходимости оценить свои обязательства. Возможно, пришло время делегировать или сбросить часть своего бремени, чтобы избежать выгорания." } },
  { id: 34, name: { en: "Page of Wands", ru: "Паж Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_11_page.png", description: { en: "Enthusiasm, exploration, discovery, free spirit.", ru: "Энтузиазм, исследование, открытие, свободный дух." }, longDescription: { en: "The Page of Wands is a free spirit, full of enthusiasm for exploration and new ideas. This card represents a message of creative opportunity or the beginning of a new adventure. Embrace your curiosity and be open to discovery. It's a time to explore your passions and express your unique creative energy.", ru: "Паж Жезлов — это свободный дух, полный энтузиазма к исследованиям и новым идеям. Эта карта представляет сообщение о творческой возможности или начале нового приключения. Примите свое любопытство и будьте открыты для открытий. Это время исследовать свои увлечения и выражать свою уникальную творческую энергию." } },
  { id: 35, name: { en: "Knight of Wands", ru: "Рыцарь Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_12_knight.png", description: { en: "Energy, passion, inspired action, adventure, impulsiveness.", ru: "Энергия, страсть, вдохновенное действие, приключение, импульсивность." }, longDescription: { en: "The Knight of Wands is a figure of immense energy, passion, and adventure. He acts on impulse and is always ready to charge into new experiences. This card signifies a time of inspired action and pursuing your goals with vigor. Be mindful of impulsiveness, but don't be afraid to embrace your adventurous spirit.", ru: "Рыцарь Жезлов — это фигура огромной энергии, страсти и приключений. Он действует импульсивно и всегда готов ринуться в новые переживания. Эта карта означает время вдохновенных действий и энергичного преследования своих целей. Остерегайтесь импульсивности, но не бойтесь принять свой авантюрный дух." } },
  { id: 36, name: { en: "Queen of Wands", ru: "Королева Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_13_queen.png", description: { en: "Courage, confidence, independence, social butterfly, determination.", ru: "Мужество, уверенность, независимость, общительность, решимость." }, longDescription: { en: "The Queen of Wands is a confident, courageous, and independent leader. She is socially adept and radiates a warm, determined energy. This card encourages you to be bold and express yourself with passion. Embrace your inner fire and lead your life with confidence and a vibrant spirit.", ru: "Королева Жезлов — уверенный, смелый и независимый лидер. Она общительна и излучает теплую, решительную энергию. Эта карта призывает вас быть смелым и выражать себя со страстью. Примите свой внутренний огонь и ведите свою жизнь с уверенностью и живым духом." } },
  { id: 37, name: { en: "King of Wands", ru: "Король Жезлов" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/wands_14_king.png", description: { en: "Natural-born leader, vision, entrepreneur, honour.", ru: "Прирожденный лидер, видение, предприниматель, честь." }, longDescription: { en: "The King of Wands is a visionary and a natural-born leader. He has a clear vision and the entrepreneurial spirit to make it a reality. This card represents mastery of one's creative passions and the ability to inspire others. It calls on you to take charge, be honorable, and lead with a bold, inspiring vision for the future.", ru: "Король Жезлов — визионер и прирожденный лидер. У него есть ясное видение и предпринимательский дух, чтобы воплотить его в реальность. Эта карта представляет мастерство владения своими творческими страстями и способность вдохновлять других. Она призывает вас взять на себя руководство, быть честным и вести за собой со смелым, вдохновляющим видением будущего." } },
  
  // Cups
  { id: 38, name: { en: "Ace of Cups", ru: "Туз Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_01_ace.png", description: { en: "Love, new relationships, compassion, creativity.", ru: "Любовь, новые отношения, сострадание, творчество." }, longDescription: { en: "The Ace of Cups is an overflowing of love, creativity, and emotional connection. It signifies the beginning of a new relationship, a deeper sense of compassion, or a surge of creative inspiration. This card invites you to open your heart and allow your emotions to flow freely. It is a powerful symbol of new emotional beginnings.", ru: "Туз Кубков — это переполняющая любовь, творчество и эмоциональная связь. Он означает начало новых отношений, более глубокое чувство сострадания или всплеск творческого вдохновения. Эта карта приглашает вас открыть свое сердце и позволить своим эмоциям течь свободно. Это мощный символ новых эмоциональных начинаний." } },
  { id: 39, name: { en: "Two of Cups", ru: "Двойка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_02_two.png", description: { en: "Unified love, partnership, mutual attraction.", ru: "Единая любовь, партнерство, взаимное притяжение." }, longDescription: { en: "The Two of Cups represents a deep, unified partnership built on mutual attraction and respect. It is a card of connection and shared love. This can be a romantic relationship, a close friendship, or a strong business partnership. It signifies a harmonious bond where both parties are on the same emotional wavelength.", ru: "Двойка Кубков представляет глубокое, единое партнерство, построенное на взаимном притяжении и уважении. Это карта связи и разделенной любви. Это могут быть романтические отношения, близкая дружба или прочное деловое партнерство. Она означает гармоничную связь, где обе стороны находятся на одной эмоциональной волне." } },
  { id: 40, name: { en: "Three of Cups", ru: "Тройка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_03_three.png", description: { en: "Celebration, friendship, creativity, collaborations.", ru: "Празднование, дружба, творчество, сотрудничество." }, longDescription: { en: "The Three of Cups is a card of joyful celebration, friendship, and community. It represents a time of happy gatherings and creative collaborations. This card encourages you to connect with your friends and celebrate your shared successes. It's a time for camaraderie and enjoying the company of your 'tribe'.", ru: "Тройка Кубков — это карта радостного празднования, дружбы и сообщества. Она представляет время счастливых встреч и творческого сотрудничества. Эта карта призывает вас общаться с друзьями и праздновать общие успехи. Это время для товарищества и наслаждения компанией своего 'племени'." } },
  { id: 41, name: { en: "Four of Cups", ru: "Четверка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_04_four.png", description: { en: "Meditation, contemplation, apathy, re-evaluation.", ru: "Медитация, созерцание, апатия, переоценка." }, longDescription: { en: "The Four of Cups signifies a period of apathy, contemplation, and re-evaluation. You may be feeling emotionally withdrawn or discontent with your current situation. A new opportunity is being offered, but you may not be ready to see it. This card asks you to look within to understand what is causing this disconnect and to be open to new possibilities.", ru: "Четверка Кубков означает период апатии, созерцания и переоценки. Вы можете чувствовать себя эмоционально отстраненным или недовольным текущей ситуацией. Вам предлагается новая возможность, но вы можете быть не готовы ее увидеть. Эта карта просит вас заглянуть внутрь, чтобы понять, что вызывает это отчуждение, и быть открытым для новых возможностей." } },
  { id: 42, name: { en: "Five of Cups", ru: "Пятерка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_05_five.png", description: { en: "Regret, failure, disappointment, pessimism.", ru: "Сожаление, неудача, разочарование, пессимизм." }, longDescription: { en: "The Five of Cups is a card of loss, regret, and disappointment. You may be focusing on what has gone wrong, ignoring the positive aspects that still remain. This card acknowledges your sorrow but also gently reminds you that not all is lost. It's a call to shift your perspective, forgive yourself, and see the opportunities that are still available.", ru: "Пятерка Кубков — это карта потерь, сожаления и разочарования. Вы можете быть сосредоточены на том, что пошло не так, игнорируя позитивные аспекты, которые все еще остаются. Эта карта признает вашу печаль, но также мягко напоминает вам, что не все потеряно. Это призыв изменить свою точку зрения, простить себя и увидеть возможности, которые все еще доступны." } },
  { id: 43, name: { en: "Six of Cups", ru: "Шестерка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_06_six.png", description: { en: "Revisiting the past, childhood memories, innocence, joy.", ru: "Возвращение в прошлое, детские воспоминания, невинность, радость." }, longDescription: { en: "The Six of Cups represents nostalgia, childhood memories, and innocence. It can signify a reunion with someone from your past or a return to a place that feels like home. This card is about simple joys and the comfort of the familiar. It encourages you to find happiness in the sweet memories of the past while remaining present.", ru: "Шестерка Кубков представляет ностальгию, детские воспоминания и невинность. Она может означать воссоединение с кем-то из вашего прошлого или возвращение в место, которое кажется домом. Эта карта говорит о простых радостях и комфорте знакомого. Она призывает вас находить счастье в сладких воспоминаниях о прошлом, оставаясь при этом в настоящем." } },
  { id: 44, name: { en: "Seven of Cups", ru: "Семерка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_07_seven.png", description: { en: "Opportunities, choices, wishful thinking, illusion.", ru: "Возможности, выбор, принятие желаемого за действительное, иллюзия." }, longDescription: { en: "The Seven of Cups presents you with many choices and opportunities, but it also warns of illusion and wishful thinking. You may be daydreaming about possibilities without taking concrete action. This card advises you to gain clarity on what you truly want and to distinguish between fantasy and viable options before making a decision.", ru: "Семерка Кубков представляет вам множество выборов и возможностей, но также предостерегает от иллюзий и принятия желаемого за действительное. Вы можете мечтать о возможностях, не предпринимая конкретных действий. Эта карта советует вам прояснить, чего вы действительно хотите, и отличить фантазии от реальных вариантов, прежде чем принимать решение." } },
  { id: 45, name: { en: "Eight of Cups", ru: "Восьмерка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_08_eight.png", description: { en: "Disappointment, abandonment, withdrawal, escapism.", ru: "Разочарование, оставление, уход, эскапизм." }, longDescription: { en: "The Eight of Cups signifies a time of walking away from something that is no longer emotionally fulfilling. It represents disappointment and the difficult decision to leave a situation behind in search of deeper meaning. This card is about withdrawal and escapism, but for a higher purpose. It's a courageous step towards finding your true path.", ru: "Восьмерка Кубков означает время ухода от того, что больше не приносит эмоционального удовлетворения. Она представляет разочарование и трудное решение оставить ситуацию позади в поисках более глубокого смысла. Эта карта говорит об уходе и эскапизме, но ради высшей цели. Это смелый шаг к нахождению своего истинного пути." } },
  { id: 46, name: { en: "Nine of Cups", ru: "Девятка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_09_nine.png", description: { en: "Contentment, satisfaction, gratitude, wish come true.", ru: "Удовлетворенность, удовлетворение, благодарность, исполнение желания." }, longDescription: { en: "Often called the 'wish card,' the Nine of Cups represents contentment, emotional fulfillment, and gratitude. It signifies that your wishes are coming true and you are enjoying a period of deep satisfaction. This card encourages you to appreciate the abundance in your life and to savor the simple pleasures. It is a symbol of personal happiness.", ru: "Часто называемая 'картой желаний', Девятка Кубков представляет удовлетворенность, эмоциональное исполнение и благодарность. Она означает, что ваши желания сбываются, и вы наслаждаетесь периодом глубокого удовлетворения. Эта карта призывает вас ценить изобилие в своей жизни и наслаждаться простыми удовольствиями. Это символ личного счастья." } },
  { id: 47, name: { en: "Ten of Cups", ru: "Десятка Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_10_ten.png", description: { en: "Divine love, blissful relationships, harmony, alignment.", ru: "Божественная любовь, блаженные отношения, гармония, выравнивание." }, longDescription: { en: "The Ten of Cups is the ultimate card of emotional fulfillment and harmonious relationships. It represents blissful family life, divine love, and a sense of belonging. This card signifies a time of lasting happiness and emotional alignment. It is a beautiful symbol of achieving true, heartfelt joy with those you love.", ru: "Десятка Кубков — это высшая карта эмоционального удовлетворения и гармоничных отношений. Она представляет блаженную семейную жизнь, божественную любовь и чувство принадлежности. Эта карта означает время прочного счастья и эмоционального выравнивания. Это прекрасный символ достижения истинной, сердечной радости с теми, кого вы любите." } },
  { id: 48, name: { en: "Page of Cups", ru: "Паж Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_11_page.png", description: { en: "Creative opportunities, intuitive messages, curiosity, possibility.", ru: "Творческие возможности, интуитивные сообщения, любопытство, возможность." }, longDescription: { en: "The Page of Cups is a gentle messenger of creative and intuitive insights. This card often appears when you are receiving an intuitive message or a new creative opportunity. It encourages you to be curious, open your heart, and explore your emotions and imagination. Listen to the whispers of your intuition.", ru: "Паж Кубков — это нежный посланник творческих и интуитивных озарений. Эта карта часто появляется, когда вы получаете интуитивное сообщение или новую творческую возможность. Она призывает вас быть любопытным, открыть свое сердце и исследовать свои эмоции и воображение. Прислушайтесь к шепоту своей интуиции." } },
  { id: 49, name: { en: "Knight of Cups", ru: "Рыцарь Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_12_knight.png", description: { en: "Creativity, romance, charm, imagination, beauty.", ru: "Творчество, романтика, обаяние, воображение, красота." }, longDescription: { en: "The Knight of Cups is a romantic, charming, and imaginative figure who is in touch with his emotions. He represents acting on your creative ideas or a romantic proposal. This card encourages you to follow your heart and express your feelings. He is a dreamer and a poet, bringing beauty and imagination into the world.", ru: "Рыцарь Кубков — это романтичная, очаровательная и творческая фигура, которая находится в контакте со своими эмоциями. Он представляет действие на основе ваших творческих идей или романтическое предложение. Эта карта призывает вас следовать своему сердцу и выражать свои чувства. Он — мечтатель и поэт, приносящий красоту и воображение в мир." } },
  { id: 50, name: { en: "Queen of Cups", ru: "Королева Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_13_queen.png", description: { en: "Compassionate, caring, emotionally stable, intuitive, in flow.", ru: "Сострадательная, заботливая, эмоционально стабильная, интуитивная, в потоке." }, longDescription: { en: "The Queen of Cups is emotionally mature, compassionate, and highly intuitive. She is in tune with her emotions and uses them as a source of strength. This card represents a caring, supportive presence. It encourages you to trust your intuition, be kind to yourself and others, and lead with your heart.", ru: "Королева Кубков эмоционально зрелая, сострадательная и очень интуитивная. Она находится в гармонии со своими эмоциями и использует их как источник силы. Эта карта представляет заботливое, поддерживающее присутствие. Она призывает вас доверять своей интуиции, быть добрым к себе и другим и руководствоваться сердцем." } },
  { id: 51, name: { en: "King of Cups", ru: "Король Кубков" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/cups_14_king.png", description: { en: "Emotionally balanced, compassionate, diplomatic.", ru: "Эмоционально уравновешенный, сострадательный, дипломатичный." }, longDescription: { en: "The King of Cups represents mastery over the emotional realm. He is emotionally balanced, compassionate, and diplomatic. He can control his feelings without suppressing them. This card signifies a need for emotional maturity and calm leadership. It's about making wise decisions that balance logic and emotion.", ru: "Король Кубков представляет мастерство над эмоциональной сферой. Он эмоционально уравновешен, сострадателен и дипломатичен. Он может контролировать свои чувства, не подавляя их. Эта карта означает потребность в эмоциональной зрелости и спокойном лидерстве. Она говорит о принятии мудрых решений, которые уравновешивают логику и эмоции." } },
  
  // Swords
  { id: 52, name: { en: "Ace of Swords", ru: "Туз Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_01_ace.png", description: { en: "Breakthroughs, new ideas, mental clarity, success.", ru: "Прорывы, новые идеи, ясность ума, успех." }, longDescription: { en: "The Ace of Swords represents a breakthrough of mental clarity and truth. It is a powerful new idea or a moment of profound insight that cuts through confusion. This card signifies success through intellectual power and clear communication. It's a time to use your sharp mind to achieve your goals.", ru: "Туз Мечей представляет прорыв ментальной ясности и истины. Это мощная новая идея или момент глубокого озарения, который прорезает путаницу. Эта карта означает успех благодаря интеллектуальной силе и четкому общению. Пришло время использовать свой острый ум для достижения целей." } },
  { id: 53, name: { en: "Two of Swords", ru: "Двойка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_02_two.png", description: { en: "Difficult decisions, weighing up options, an impasse, avoidance.", ru: "Трудные решения, взвешивание вариантов, тупик, избегание." }, longDescription: { en: "The Two of Swords signifies being at an impasse, facing a difficult decision. You may be avoiding the choice by blocking out your emotions or external information. This card calls for a truce and a balanced assessment of the situation. You need to weigh your options carefully, but a decision must eventually be made.", ru: "Двойка Мечей означает нахождение в тупике, перед трудным решением. Вы можете избегать выбора, блокируя свои эмоции или внешнюю информацию. Эта карта призывает к перемирию и сбалансированной оценке ситуации. Вам нужно тщательно взвесить варианты, но в конечном итоге решение должно быть принято." } },
  { id: 54, name: { en: "Three of Swords", ru: "Тройка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_03_three.png", description: { en: "Heartbreak, emotional pain, sorrow, grief, hurt.", ru: "Разбитое сердце, эмоциональная боль, печаль, горе, обида." }, longDescription: { en: "The Three of Swords is a card of heartbreak, sorrow, and painful truth. It represents a difficult realization or emotional pain that cannot be avoided. While this card is painful, it is also a necessary part of healing. Acknowledging the truth, however hurtful, allows you to process the pain and eventually move forward.", ru: "Тройка Мечей — это карта разбитого сердца, печали и болезненной правды. Она представляет трудное осознание или эмоциональную боль, которой невозможно избежать. Хотя эта карта болезненна, она также является необходимой частью исцеления. Признание правды, какой бы обидной она ни была, позволяет вам пережить боль и в конечном итоге двигаться дальше." } },
  { id: 55, name: { en: "Four of Swords", ru: "Четверка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_04_four.png", description: { en: "Rest, relaxation, meditation, contemplation, recuperation.", ru: "Отдых, расслабление, медитация, созерцание, восстановление." }, longDescription: { en: "The Four of Swords signifies a necessary period of rest, recuperation, and contemplation. After a period of conflict or mental stress, this card calls for a strategic retreat. It's a time for meditation and quiet reflection to recharge your mental batteries. This is a pause, not a surrender.", ru: "Четверка Мечей означает необходимый период отдыха, восстановления и созерцания. После периода конфликта или умственного напряжения эта карта призывает к стратегическому отступлению. Это время для медитации и тихого размышления, чтобы перезарядить свои умственные батареи. Это пауза, а не сдача." } },
  { id: 56, name: { en: "Five of Swords", ru: "Пятерка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_05_five.png", description: { en: "Conflict, competition, defeat, winning at all costs.", ru: "Конфликт, соревнование, поражение, победа любой ценой." }, longDescription: { en: "The Five of Swords represents conflict and a win-at-all-costs mentality. It can signify a hollow victory, where the cost of winning was too high, leading to alienation and resentment. This card warns against pointless battles and urges you to consider the consequences of your actions. Sometimes, the wisest move is to walk away.", ru: "Пятерка Мечей представляет конфликт и менталитет 'победа любой ценой'. Она может означать пустую победу, где цена победы была слишком высока, что привело к отчуждению и обиде. Эта карта предостерегает от бессмысленных сражений и призывает вас учитывать последствия своих действий. Иногда самый мудрый шаг — это уйти." } },
  { id: 57, name: { en: "Six of Swords", ru: "Шестерка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_06_six.png", description: { en: "Transition, change, rite of passage, releasing baggage.", ru: "Переход, изменение, обряд посвящения, освобождение от багажа." }, longDescription: { en: "The Six of Swords signifies a transition, moving from a difficult past towards a calmer future. It represents releasing old baggage and making a necessary journey, either literally or metaphorically. While there may be some sadness in leaving things behind, this card assures you that you are moving in the right direction towards healing and peace.", ru: "Шестерка Мечей означает переход, движение от трудного прошлого к более спокойному будущему. Она представляет освобождение от старого багажа и совершение необходимого путешествия, будь то буквально или метафорически. Хотя может быть некоторая грусть от расставания, эта карта уверяет вас, что вы движетесь в правильном направлении к исцелению и миру." } },
  { id: 58, name: { en: "Seven of Swords", ru: "Семерка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_07_seven.png", description: { en: "Betrayal, deception, getting away with something, acting strategically.", ru: "Предательство, обман, выход сухим из воды, стратегические действия." }, longDescription: { en: "The Seven of Swords warns of deception, betrayal, or acting strategically to get away with something. It can represent someone acting alone, not being a team player, or using cunning to their advantage. This card calls for caution and honesty. Be aware of your own actions and the actions of those around you.", ru: "Семерка Мечей предостерегает от обмана, предательства или стратегических действий, чтобы выйти сухим из воды. Она может представлять кого-то, кто действует в одиночку, не является командным игроком или использует хитрость в своих интересах. Эта карта призывает к осторожности и честности. Будьте внимательны к своим собственным действиям и действиям окружающих." } },
  { id: 59, name: { en: "Eight of Swords", ru: "Восьмерка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_08_eight.png", description: { en: "Negative thoughts, self-imposed restriction, imprisonment, victim mentality.", ru: "Негативные мысли, самоограничение, заключение, менталитет жертвы." }, longDescription: { en: "The Eight of Swords represents feeling trapped and restricted, but these limitations are often self-imposed. It points to a victim mentality and the power of negative thoughts to create your own prison. This card shows that you have the power to free yourself by changing your perspective and challenging your limiting beliefs.", ru: "Восьмерка Мечей представляет чувство ловушки и ограничения, но эти ограничения часто навязаны самим себе. Она указывает на менталитет жертвы и силу негативных мыслей в создании собственной тюрьмы. Эта карта показывает, что у вас есть сила освободиться, изменив свою точку зрения и бросив вызов своим ограничивающим убеждениям." } },
  { id: 60, name: { en: "Nine of Swords", ru: "Девятка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_09_nine.png", description: { en: "Anxiety, worry, fear, depression, nightmares.", ru: "Тревога, беспокойство, страх, депрессия, кошмары." }, longDescription: { en: "The Nine of Swords is the card of anxiety, fear, and worry. It often represents mental anguish and sleepless nights spent overthinking problems. These fears may be exaggerated by your mind. This card is a sign that you need to address what is causing this distress and seek support. Don't suffer in silence.", ru: "Девятка Мечей — это карта тревоги, страха и беспокойства. Она часто представляет душевные муки и бессонные ночи, проведенные в размышлениях о проблемах. Эти страхи могут быть преувеличены вашим разумом. Эта карта — знак того, что вам нужно разобраться с причиной этого страдания и обратиться за поддержкой. Не страдайте молча." } },
  { id: 61, name: { en: "Ten of Swords", ru: "Десятка Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_10_ten.png", description: { en: "Painful endings, deep wounds, betrayal, loss, crisis.", ru: "Болезненные окончания, глубокие раны, предательство, потеря, кризис." }, longDescription: { en: "The Ten of Swords represents a painful and final ending. It can signify betrayal, deep wounds, or hitting rock bottom. While this card is dramatic and difficult, it also marks the end of a cycle of pain. Once you have hit the lowest point, the only way is up. It is a sign that the worst is over, and healing can begin.", ru: "Десятка Мечей представляет болезненное и окончательное завершение. Она может означать предательство, глубокие раны или достижение дна. Хотя эта карта драматична и трудна, она также знаменует конец цикла боли. Как только вы достигли самой низкой точки, единственный путь — наверх. Это знак того, что худшее позади, и может начаться исцеление." } },
  { id: 62, name: { en: "Page of Swords", ru: "Паж Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_11_page.png", description: { en: "New ideas, curiosity, thirst for knowledge, new ways of communicating.", ru: "Новые идеи, любопытство, жажда знаний, новые способы общения." }, longDescription: { en: "The Page of Swords is full of curiosity and a thirst for knowledge. This card represents new ideas, a desire to learn, and new ways of communicating. It encourages you to ask questions, seek the truth, and stay mentally agile. Be open to new information and don't be afraid to speak your mind.", ru: "Паж Мечей полон любопытства и жажды знаний. Эта карта представляет новые идеи, желание учиться и новые способы общения. Она призывает вас задавать вопросы, искать истину и оставаться умственно гибким. Будьте открыты для новой информации и не бойтесь высказывать свое мнение." } },
  { id: 63, name: { en: "Knight of Swords", ru: "Рыцарь Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_12_knight.png", description: { en: "Ambitious, action-oriented, driven to succeed, fast-thinking.", ru: "Амбициозный, ориентированный на действие, стремящийся к успеху, быстро соображающий." }, longDescription: { en: "The Knight of Swords is ambitious, fast-thinking, and driven to succeed. He charges forward with immense focus and determination. This card signifies a time to be assertive and to pursue your goals with unwavering ambition. However, it also warns against being overly hasty or disregarding the feelings of others in your quest.", ru: "Рыцарь Мечей амбициозен, быстро соображает и стремится к успеху. Он несется вперед с огромной сосредоточенностью и решимостью. Эта карта означает время быть настойчивым и преследовать свои цели с непоколебимой амбицией. Однако она также предостерегает от излишней поспешности или пренебрежения чувствами других в своем стремлении." } },
  { id: 64, name: { en: "Queen of Swords", ru: "Королева Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_13_queen.png", description: { en: "Independent, unbiased judgement, clear boundaries, direct communication.", ru: "Независимая, беспристрастное суждение, ясные границы, прямое общение." }, longDescription: { en: "The Queen of Swords is an independent thinker with a sharp mind and clear boundaries. She values truth and direct communication. This card encourages you to use your unbiased judgment and to communicate your needs clearly. She is a master of setting boundaries and cutting through deception with her intellectual clarity.", ru: "Королева Мечей — независимый мыслитель с острым умом и четкими границами. Она ценит истину и прямое общение. Эта карта призывает вас использовать свое беспристрастное суждение и четко сообщать о своих потребностях. Она — мастер установления границ и разоблачения обмана с помощью своей интеллектуальной ясности." } },
  { id: 65, name: { en: "King of Swords", ru: "Король Мечей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/swords_14_king.png", description: { en: "Mental clarity, intellectual power, authority, truth.", ru: "Ясность ума, интеллектуальная мощь, авторитет, истина." }, longDescription: { en: "The King of Swords represents intellectual mastery, authority, and truth. He is a clear-thinking leader who makes decisions based on logic and fairness. This card signifies a time to use your intellect and authority to bring clarity and structure to a situation. It is about leading with integrity and speaking the truth with power.", ru: "Король Мечей представляет интеллектуальное мастерство, авторитет и истину. Он — ясномыслящий лидер, который принимает решения на основе логики и справедливости. Эта карта означает время использовать свой интеллект и авторитет, чтобы внести ясность и структуру в ситуацию. Она говорит о лидерстве с честностью и мощном изложении правды." } },

  // Pentacles
  { id: 66, name: { en: "Ace of Pentacles", ru: "Туз Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_01_ace.png", description: { en: "A new financial or career opportunity, manifestation, abundance.", ru: "Новая финансовая или карьерная возможность, проявление, изобилие." }, longDescription: { en: "The Ace of Pentacles is a seed of prosperity and manifestation. It represents a new opportunity in the material world, such as a new job, a financial gift, or the start of a prosperous venture. This card encourages you to ground your ambitions and take practical steps to turn them into reality. It is a sign of abundance and new beginnings.", ru: "Туз Пентаклей — это семя процветания и проявления. Он представляет новую возможность в материальном мире, такую как новая работа, финансовый подарок или начало процветающего предприятия. Эта карта призывает вас заземлить свои амбиции и предпринять практические шаги, чтобы превратить их в реальность. Это знак изобилия и новых начинаний." } },
  { id: 67, name: { en: "Two of Pentacles", ru: "Двойка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_02_two.png", description: { en: "Multiple priorities, time management, prioritisation, adaptability.", ru: "Множество приоритетов, управление временем, приоритизация, адаптивность." }, longDescription: { en: "The Two of Pentacles is about juggling multiple priorities and maintaining balance. It signifies the need for adaptability and effective time management. You may be balancing work, finances, and personal life. This card encourages you to be flexible and to go with the flow as you navigate your various commitments.", ru: "Двойка Пентаклей говорит о жонглировании множеством приоритетов и поддержании баланса. Она означает необходимость в адаптивности и эффективном управлении временем. Вы можете балансировать между работой, финансами и личной жизнью. Эта карта призывает вас быть гибким и плыть по течению, управляя своими различными обязательствами." } },
  { id: 68, name: { en: "Three of Pentacles", ru: "Тройка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_03_three.png", description: { en: "Teamwork, collaboration, learning, implementation.", ru: "Командная работа, сотрудничество, обучение, реализация." }, longDescription: { en: "The Three of Pentacles is a card of teamwork, collaboration, and skilled craftsmanship. It signifies a situation where different people come together to work on a common goal. This card highlights the importance of collaboration and learning from others. Your skills are recognized, and through teamwork, you can achieve great results.", ru: "Тройка Пентаклей — это карта командной работы, сотрудничества и искусного мастерства. Она означает ситуацию, когда разные люди объединяются для работы над общей целью. Эта карта подчеркивает важность сотрудничества и обучения у других. Ваши навыки признаны, и благодаря командной работе вы можете достичь великолепных результатов." } },
  { id: 69, name: { en: "Four of Pentacles", ru: "Четверка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_04_four.png", description: { en: "Saving money, security, conservatism, scarcity, control.", ru: "Экономия денег, безопасность, консерватизм, дефицит, контроль." }, longDescription: { en: "The Four of Pentacles represents a focus on security, control, and saving money. You may be holding on tightly to your resources out of a fear of lack. This card can indicate a need for stability, but it also warns against becoming too conservative or possessive. It asks you to examine your relationship with material wealth and whether it is serving or restricting you.", ru: "Четверка Пентаклей представляет сосредоточенность на безопасности, контроле и экономии денег. Вы можете крепко держаться за свои ресурсы из-за страха нехватки. Эта карта может указывать на потребность в стабильности, но также предостерегает от излишнего консерватизма или собственничества. Она просит вас изучить ваши отношения с материальным богатством и понять, служит ли оно вам или ограничивает." } },
  { id: 70, name: { en: "Five of Pentacles", ru: "Пятерка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_05_five.png", description: { en: "Financial loss, poverty, lack mindset, isolation, worry.", ru: "Финансовые потери, бедность, мышление нехватки, изоляция, беспокойство." }, longDescription: { en: "The Five of Pentacles signifies a time of financial hardship, isolation, and worry. You may be feeling left out in the cold, experiencing a sense of lack. This card is a reminder that help is often available, even if you don't see it. It encourages you to release your pride and ask for support when you need it.", ru: "Пятерка Пентаклей означает время финансовых трудностей, изоляции и беспокойства. Вы можете чувствовать себя оставленным за бортом, испытывая чувство нехватки. Эта карта — напоминание о том, что помощь часто доступна, даже если вы ее не видите. Она призывает вас отбросить гордость и попросить поддержки, когда она вам нужна." } },
  { id: 71, name: { en: "Six of Pentacles", ru: "Шестерка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_06_six.png", description: { en: "Giving, receiving, sharing wealth, generosity, charity.", ru: "Дарение, получение, разделение богатства, щедрость, благотворительность." }, longDescription: { en: "The Six of Pentacles is a card of generosity, charity, and the flow of giving and receiving. It can represent being in a position to help others, or being the recipient of needed support. This card highlights the importance of sharing wealth and creating a balance between what you give and what you take.", ru: "Шестерка Пентаклей — это карта щедрости, благотворительности и потока дарения и получения. Она может представлять как положение, в котором вы можете помочь другим, так и получение необходимой поддержки. Эта карта подчеркивает важность разделения богатства и создания баланса между тем, что вы даете, и тем, что вы берете." } },
  { id: 72, name: { en: "Seven of Pentacles", ru: "Семерка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_07_seven.png", description: { en: "Long-term view, sustainable results, perseverance, investment.", ru: "Долгосрочная перспектива, устойчивые результаты, настойчивость, инвестиции." }, longDescription: { en: "The Seven of Pentacles represents a moment of reflection on your long-term investments. You have worked hard, and now it's time to pause and assess your progress. This card signifies perseverance and the importance of a sustainable approach. It encourages you to take a long-term view and have patience as your efforts come to fruition.", ru: "Семерка Пентаклей представляет момент размышления о ваших долгосрочных инвестициях. Вы усердно трудились, и теперь пришло время сделать паузу и оценить свой прогресс. Эта карта означает настойчивость и важность устойчивого подхода. Она призывает вас смотреть на долгосрочную перспективу и проявлять терпение, пока ваши усилия приносят плоды." } },
  { id: 73, name: { en: "Eight of Pentacles", ru: "Восьмерка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_08_eight.png", description: { en: "Apprenticeship, repetitive tasks, mastery, skill development.", ru: "Ученичество, повторяющиеся задачи, мастерство, развитие навыков." }, longDescription: { en: "The Eight of Pentacles is a card of diligent work, skill development, and mastery. It represents a commitment to improving your craft through repetitive tasks and dedication. This card encourages you to focus on the details and take pride in your work. Through hard work and practice, you are on the path to becoming a master of your skill.", ru: "Восьмерка Пентаклей — это карта усердной работы, развития навыков и мастерства. Она представляет приверженность совершенствованию своего ремесла через повторяющиеся задачи и преданность делу. Эта карта призывает вас сосредоточиться на деталях и гордиться своей работой. Благодаря усердному труду и практике вы находитесь на пути к тому, чтобы стать мастером своего дела." } },
  { id: 74, name: { en: "Nine of Pentacles", ru: "Девятка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_09_nine.png", description: { en: "Abundance, luxury, self-sufficiency, financial independence.", ru: "Изобилие, роскошь, самодостаточность, финансовая независимость." }, longDescription: { en: "The Nine of Pentacles signifies luxury, abundance, and financial independence achieved through your own efforts. It represents a time of enjoying the fruits of your labor in comfort and security. This card celebrates self-sufficiency and the pleasure of having created a stable and prosperous life for yourself.", ru: "Девятка Пентаклей означает роскошь, изобилие и финансовую независимость, достигнутые вашими собственными усилиями. Она представляет время наслаждения плодами своего труда в комфорте и безопасности. Эта карта прославляет самодостаточность и удовольствие от создания стабильной и процветающей жизни для себя." } },
  { id: 75, name: { en: "Ten of Pentacles", ru: "Десятка Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_10_ten.png", description: { en: "Wealth, financial security, family, long-term success, contribution.", ru: "Богатство, финансовая безопасность, семья, долгосрочный успех, вклад." }, longDescription: { en: "The Ten of Pentacles represents long-term success, financial security, and the establishment of a lasting legacy. It is a card of family, tradition, and passing wealth down through generations. This card signifies that you have built something solid and enduring that will support you and your loved ones for years to come.", ru: "Десятка Пентаклей представляет долгосрочный успех, финансовую безопасность и создание прочного наследия. Это карта семьи, традиций и передачи богатства из поколения в поколение. Эта карта означает, что вы построили что-то прочное и долговечное, что будет поддерживать вас и ваших близких долгие годы." } },
  { id: 76, name: { en: "Page of Pentacles", ru: "Паж Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_11_page.png", description: { en: "Manifestation, financial opportunity, skill development.", ru: "Проявление, финансовая возможность, развитие навыков." }, longDescription: { en: "The Page of Pentacles is a messenger of new opportunities in the material world. This card represents a desire to learn a new skill or start a new project that can lead to prosperity. It encourages you to be grounded, practical, and diligent in pursuing your goals. It is a symbol of manifestation and tangible beginnings.", ru: "Паж Пентаклей — это посланник новых возможностей в материальном мире. Эта карта представляет желание освоить новый навык или начать новый проект, который может привести к процветанию. Она призывает вас быть приземленным, практичным и усердным в достижении своих целей. Это символ проявления и ощутимых начинаний." } },
  { id: 77, name: { en: "Knight of Pentacles", ru: "Рыцарь Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_12_knight.png", description: { en: "Hard work, productivity, routine, conservatism.", ru: "Тяжелая работа, производительность, рутина, консерватизм." }, longDescription: { en: "The Knight of Pentacles is the most hardworking and reliable of the knights. He represents a methodical and routine approach to achieving his goals. This card signifies productivity and a commitment to your duties. While he may be conservative and slow-moving, his persistence ensures that he will eventually reach his destination.", ru: "Рыцарь Пентаклей — самый трудолюбивый и надежный из рыцарей. Он представляет методичный и рутинный подход к достижению своих целей. Эта карта означает производительность и приверженность своим обязанностям. Хотя он может быть консервативным и медлительным, его настойчивость гарантирует, что он в конечном итоге достигнет своей цели." } },
  { id: 78, name: { en: "Queen of Pentacles", ru: "Королева Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_13_queen.png", description: { en: "Nurturing, practical, providing financially, a working parent.", ru: "Заботливая, практичная, обеспечивающая финансово, работающий родитель." }, longDescription: { en: "The Queen of Pentacles is a nurturing and practical figure who creates a warm and secure environment. She is adept at managing both her home and her work life. This card represents providing for others in a practical way and creating a sense of stability. She encourages a down-to-earth and generous approach to life.", ru: "Королева Пентаклей — заботливая и практичная фигура, которая создает теплую и безопасную обстановку. Она умело управляет как домом, так и работой. Эта карта представляет практическую заботу о других и создание чувства стабильности. Она поощряет приземленный и щедрый подход к жизни." } },
  { id: 79, name: { en: "King of Pentacles", ru: "Король Пентаклей" }, imageUrl: "https://cdn.jsdelivr.net/gh/marataitester-blip/tarot/pentacles_14_king.png", description: { en: "Wealth, business, leadership, security, discipline, abundance.", ru: "Богатство, бизнес, лидерство, безопасность, дисциплина, изобилие." }, longDescription: { en: "The King of Pentacles is a master of the material world, representing wealth, security, and business leadership. He has achieved great success through his discipline and practical wisdom. This card signifies abundance and the ability to create prosperity not just for himself, but for others as well. He is a provider and a symbol of reliable success.", ru: "Король Пентаклей — это мастер материального мира, представляющий богатство, безопасность и деловое лидерство. Он достиг большого успеха благодаря своей дисциплине и практической мудрости. Эта карта означает изобилие и способность создавать процветание не только для себя, но и для других. Он — кормилец и символ надежного успеха." } }
];

export default tarotDeck;